"use strict";$(function(){$("#loginFrom").validate({rules:{username:{required:!0,minlength:5},password:{required:!0,minlength:5},confirm:{equalTo:"#password"}},showErrors:function(e,r){0!=r.length&&$(".err").empty().text(r[0].message)},messages:{username:{required:"请输入用户名",minlength:"用户名必需由五个字母组成"},password:{required:"请输入密码",minlength:"密码长度不能小于 5 个字母"},confirm:{required:"请确认密码",equalTo:"两次密码输入不一致"}},onfocusout:!1,errorPlacement:function(e,r){e.appendTo($(".err"))},submitHandler:function(e){$.ajax({url:"/register",type:"post",data:$(e).serialize(),success:function(e){"1"==e&&(window.location="http://localhost:8080/pages/login.html")},error:function(e){console.log(e)}})}})});